-- for more security, you could load these from your env variables (advanced)
TOKEN = 'yourtoken' -- bot token
GUILD_ID = 'yourserverid' -- discord server id

CardTemplate = {
    type = "AdaptiveCard",
    body = {
        {
            type = "TextBlock",
            size = "ExtraLarge",
            weight = "Bolder",
            text = "Attention!"
        },
        {
            type = "TextBlock",
            size = "Medium",
            spacing = "Small",
            text = "We noticed that you didn't join our Discord yet. Click the button below to join! Once you have joined, click Try again to continue.",
            wrap = true
        },
        {
            type = "ActionSet",
            spacing = "Medium",
            actions = {
                {
                    type = "Action.OpenUrl",
                    url = "https://discord.gg/YOUR_URL",
                    title = "Join our Discord",
                    iconUrl = "data:image/png;base64,"
                },
                {
                    type = "Action.Submit",
                    title = "Try again",
                    data = {retry = "true"},
                    iconUrl = "data:image/png;base64,"
                }
            }
        }
    },
    ["$schema"] = "http://adaptivecards.io/schemas/adaptive-card.json", -- don't touch this
    version = "1.0" -- don't touch this either
}